"use strict";!function getSong(){var $songList=$("ol#songs");function getQuery(){var query=new AV.Query("Song");return query.find()}function fillSongs(Songs){for(var i=0;i<Songs.length;i++){var song=Songs[i].attributes;$songList.append(template(song))}}function template(song){return'<li data-id="'+song.id+'">\n            <div class="songBaseInfo">\n                <h3>'+song.musicName+"</h3>\n                <p><i></i>"+song.musicAuthor+'</p>\n            </div>\n            <a href="'+song.musicUrl+'"></a>\n        </li>'}getQuery().then(fillSongs,function(){console.log("出错了")})}();
